    convertPermitDetailsResponse(response) {
      let permitNavigationDataModel = new PermitNavigationDataModel();

      if (!response) {
        return permitNavigationDataModel;
      }

      permitNavigationDataModel.mapPageData = this.convertToMapPageData(response); //set conditionPageData

      permitNavigationDataModel.conditionPageData = this.convertToConditionPageData(response); //set permitFormPageData

      permitNavigationDataModel.permitFormPageData = this.convertToPermitForm(response);

      let userInfo = this._appRepoHelperService.getMDataByKey(MASTER_DATA.USER_INFO); // user Info
      // permitNavigationDataModel.permitFormPageData.orgId = userInfo.org_id;


