      if (this.alterationApplicationId != null) {
        console.log("data", data);

        this._serverApi.put(`/api/v1/applications/${applicationId}/alterations/${this.alterationApplicationId}`, data).subscribe(response => {
          try {
            this.hideSpinner(); //  this.page_refreshData();

