      let permitFormData = this.globalParameters.get(GLOBAL_PARAM_KEY.VIEW_PERMIT_FORM);
      this.progresstoPAFormGroup.controls.proposedStartDatePA.setValue(permitFormData === null || permitFormData === void 0 ? void 0 : permitFormData.proposed_start_date);
      this.progresstoPAFormGroup.controls.proposedEndDatePA.setValue(permitFormData === null || permitFormData === void 0 ? void 0 : permitFormData.proposed_end_date);
      this.validateAndShowProgressToPAWarning();
